def convertChain(titles):
    titles = titles.split("->")
    resWords = titles[0].split()
    for title in titles[1:]:
        words = title.split()
        n=0
        while n < len(words) and words[n] != resWords[-1]:
            n = n+1
        resWords += words[n+1:]
    return " ".join(resWords)
        
